import React, { useState, useEffect } from 'react';
import { Topbar } from '../Components/Topbar/topbar';
import { Footer } from '../Components/Footer/footer';
import './pages.css';

export const MainPage = () => {
  // 슬라이드에 사용할 이미지 목록 (원하는 이미지 경로를 추가)
  const images = [
    '/images/banner/dis2024/img1.jpg',
    '/images/banner/dis2024/img2.jpg',
    '/images/banner/dis2024/img3.jpg',
    '/images/banner/dis2024/img4.jpg',
    '/images/banner/dis2024/img5.jpg',
    '/images/banner/dis2024/img6.jpg',
    '/images/banner/dis2024/img7.jpg'
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // 3초 간격으로 현재 인덱스를 업데이트하여 한 번에 한 이미지씩 전환
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [images.length]);

  return (
    <div className="mainPage">
      <Topbar />
      
      <div className="banner">
        <div className="slider-container">
          <div
            className="slider"
            style={{
              transform: `translateX(-${currentImageIndex * 100}%)`
            }}
          >
            {images.map((src, index) => (
              <img key={index} src={src} alt={`banner ${index + 1}`} />
            ))}
          </div>
        </div>
        <div className="banner-text">dis 2024</div>
      </div>
      
      <Footer />
    </div>
  );
};
