import React, { useState, useEffect } from 'react';
import { Topbar } from '../Components/Topbar/topbar';
import { Footer } from '../Components/Footer/footer';
import './pages.css';

export const MainPage = () => {
  // 슬라이드에 사용할 이미지 목록
  const images = [
    '/images/banner/dis2024/img1.jpg',
    '/images/banner/dis2024/img2.jpg',
    '/images/banner/dis2024/img3.jpg'
    // 필요하다면 추가
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // 일정 시간 간격(3초)으로 슬라이드 전환
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [images.length]);

  return (
    <>
      <Topbar />
      <div className="banner">
        {/* 슬라이더를 감싸는 컨테이너 (가운데 정렬 및 width: 50%) */}
        <div className="slider-container">
          {/* 슬라이더 전체 영역(가로로 길게 늘어놓고, translateX로 이동) */}
          <div
            className="slider"
            style={{
              width: `${images.length * 100}%`,
              transform: `translateX(-${currentImageIndex * 100}%)`,
            }}
          >
            {images.map((src, index) => (
              <img key={index} src={src} alt={`banner ${index + 1}`} />
            ))}
          </div>
        </div>

        {/* 원하는 텍스트 */}
        <div className="banner-text">
          dis 2024
        </div>
      </div>
      <Footer />
    </>
  );
};
