import React, { useState, useEffect } from 'react';
import { Topbar } from '../Components/Topbar/topbar';
import { Footer } from '../Components/Footer/footer';
import './pages.css';

export const MainPage = () => {
  // 원본 이미지 목록
  const images = [
    '/images/banner/dis2024/img1.jpg',
    '/images/banner/dis2024/img2.jpg',
    '/images/banner/dis2024/img3.jpg'
  ];

  // 무한 루프를 위해 이미지 배열을 복제 (예: [img1, img2, img3, img1, img2, img3])
  const sliderImages = [...images, ...images];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [transitionEnabled, setTransitionEnabled] = useState(true);

  // 5초마다 한 슬라이드씩 전환
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prev => prev + 1);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // currentIndex가 원본 이미지 개수와 같아지면(즉, 두 번째 세트 시작) 전환 효과 후 순간적으로 리셋
  useEffect(() => {
    if (currentIndex === images.length) {
      // 전환 효과(1초)가 끝난 후 즉시 transition을 끄고 0으로 리셋
      const timeout = setTimeout(() => {
        setTransitionEnabled(false);
        setCurrentIndex(0);
      }, 1000); // 전환 효과 시간 (1초)
      return () => clearTimeout(timeout);
    } else {
      setTransitionEnabled(true);
    }
  }, [currentIndex, images.length]);

  return (
    <div className="mainPage">
      <Topbar />

      <div className="banner">
        <div className="slider-container">
          <div
            className="slider"
            style={{
              width: `${sliderImages.length * 100}%`,
              transform: `translateX(-${currentIndex * 100}%)`,
              transition: transitionEnabled ? 'transform 1s ease-in-out' : 'none'
            }}
          >
            {sliderImages.map((src, index) => (
              <div className="slide" key={index}>
                <img src={src} alt={`Slide ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>
        <div className="banner-text">dis 2024</div>
      </div>

      <Footer />
    </div>
  );
};
