import React, { useState, useEffect } from 'react';
import { Topbar } from '../Components/Topbar/topbar';
import { Footer } from '../Components/Footer/footer';
import './pages.css';

export const MainPage = () => {
  // 슬라이드에 사용할 원본 이미지 목록
  const images = [
    '/images/banner/dis2024/img1.jpg',
    '/images/banner/dis2024/img2.jpg',
    '/images/banner/dis2024/img3.jpg'
  ];

  // 클론 이미지 추가: 마지막에 첫번째 이미지를 복사
  const sliderImages = [...images, images[0]];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [transitionEnabled, setTransitionEnabled] = useState(true);

  // 3초 간격으로 다음 이미지로 전환
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => prevIndex + 1);
    }, 3000);

    return () => clearInterval(intervalId);
  }, []);

  // 마지막(클론) 이미지에 도달했을 때 전환 효과 없이 첫 번째 이미지로 점프
  useEffect(() => {
    if (currentImageIndex === sliderImages.length - 1) {
      const timeout = setTimeout(() => {
        setTransitionEnabled(false);
        setCurrentImageIndex(0);
      }, 500); // 전환 시간과 동일한 0.5초 후 실행
      return () => clearTimeout(timeout);
    } else {
      setTransitionEnabled(true);
    }
  }, [currentImageIndex, sliderImages.length]);

  return (
    <div className="mainPage">
      <Topbar />

      <div className="banner">
        <div className="slider-container">
          <div
            className="slider"
            style={{
              width: `${sliderImages.length * 100}%`,
              transform: `translateX(-${currentImageIndex * 100}%)`,
              transition: transitionEnabled ? 'transform 0.5s ease-in-out' : 'none',
            }}
          >
            {sliderImages.map((src, index) => (
              <img key={index} src={src} alt={`banner ${index + 1}`} />
            ))}
          </div>
        </div>
        <div className="banner-text">dis 2024</div>
      </div>

      <Footer />
    </div>
  );
};
